package gamezone;
import java.util.Scanner;
import java.util.*;
import javax.swing.JOptionPane;
public class RockPaperScissors {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		final int GAME_CHOICES = 4;
		String choice;
		int rock;
		int paper;
		int scissors;
		int revolver;
		int gameChoice;
		
		Scanner input = new Scanner(System.in);
		
		System.out.print("Would you like to play? Yes or No>>>");
		choice = input.nextLine();
		if(choice.equals("yes")||choice.equals("Yes")||choice.equals("Y")||choice.equals("y"))
		{
			System.out.print("Rock, paper, scissors SHOOT!");
			gameChoice = ((int)(Math.random() * 100) % GAME_CHOICES + 1);
			
			
		}
		if(gameChoice==1)
		{
			System.out.print("Rock!");
		}
		if(gameChoice==2)
		{
			System.out.print("Scissors!");
		}
		if(gameChoice==3)
		{
			System.out.print("Paper!");
		}
		if(gameChoice==4)
		{
			System.out.print("BANG Revolver.");
		}
	}

}
